// ============================================================
// SISTEMA POMODORO AUTOM√ÅTICO CON DETECCI√ìN DE PRESENCIA
// Versi√≥n Mejorada con:
// - Long breaks cada 4 ciclos
// - Reset diario autom√°tico
// - Estad√≠sticas de productividad
// - Modo No Molestar
// ============================================================

// === CONFIGURACI√ìN B√ÅSICA ===
let WORK_MINUTES = flow.get("WORK_MINUTES") || 25;
let BREAK_MINUTES = flow.get("BREAK_MINUTES") || 5;
let LONG_BREAK_MINUTES = flow.get("LONG_BREAK_MINUTES") || 15;
let WORK_SECONDS = WORK_MINUTES * 60;
let BREAK_SECONDS = BREAK_MINUTES * 60;
let LONG_BREAK_SECONDS = LONG_BREAK_MINUTES * 60;

const CYCLES_BEFORE_LONG_BREAK = 4;     // Pomodoros antes de descanso largo
const DIST_UMBRAL_CM = 60;
const CONFIRM_COUNT = 3;
const BREAK_GRACE_SECONDS = 10;

// === RECUPERAR ESTADO ===
let state = flow.get("pomodoroState") || "IDLE";
let presence = flow.get("pomodoroPresence") || false;
let presentStreak = flow.get("presentStreak") || 0;
let absentStreak = flow.get("absentStreak") || 0;
let workRemaining = flow.get("workRemaining") || 0;
let breakRemaining = flow.get("breakRemaining") || 0;
let breakPaused = flow.get("breakPaused") || false;
let breakPresenceHold = flow.get("breakPresenceHold") || 0;
let isLongBreak = flow.get("isLongBreak") || false;

// === SENSORES ===
let lastPir = flow.get("lastPir") || false;
let lastDistance = flow.get("lastDistance") || 999;

// === ESTAD√çSTICAS Y CICLOS ===
let todayDate = new Date().toISOString().split('T')[0]; // YYYY-MM-DD
let lastDate = flow.get("lastStatsDate") || todayDate;

// Reset diario autom√°tico
if (lastDate !== todayDate) {
    flow.set("cyclesToday", 0);
    flow.set("minutesWorkedToday", 0);
    flow.set("lastStatsDate", todayDate);
}

let cyclesToday = flow.get("cyclesToday") || 0;
let cyclesSinceBreak = flow.get("cyclesSinceBreak") || 0;
let minutesWorkedToday = flow.get("minutesWorkedToday") || 0;

// === MODO NO MOLESTAR ===
let doNotDisturb = flow.get("doNotDisturb") || false;
let dndUntil = flow.get("dndUntil") || 0; // timestamp

// === MENSAJES DE SALIDA ===
let msgLcd = null;
let msgBuzzer = null;
let msgUi = null;
let msgGauge = null;
let msgCycles = null;
let msgNotification = null;

// ============================================================
// IDENTIFICAR TIPO DE MENSAJE
// ============================================================
let messageType = "UNKNOWN";

if (typeof msg.topic === 'string') {
    if (msg.topic === "CONFIG_WORK" || msg.topic === "CONFIG_BREAK" ||
        msg.topic === "CONFIG_LONG_BREAK" || msg.topic === "CONFIG_BUZZER") {
        messageType = "CONFIG";
    } else if (msg.topic.startsWith("pomodoro/sensor/")) {
        messageType = "SENSOR";
    } else if (msg.topic === "TICK_1S") {
        messageType = "TICK";
    }
}

if (typeof msg.payload === 'string' && msg.payload.startsWith("CMD_")) {
    messageType = "COMMAND";
}

// ============================================================
// 1. CONFIGURACI√ìN
// ============================================================
if (messageType === "CONFIG") {
    if (msg.topic === "CONFIG_WORK") {
        let minutos = parseInt(msg.payload);
        if (!isNaN(minutos) && minutos > 0 && minutos <= 120) {
            flow.set("WORK_MINUTES", minutos);
            WORK_MINUTES = minutos;
            WORK_SECONDS = minutos * 60;
            if (state === "IDLE") {
                msgNotification = { payload: `‚öôÔ∏è Trabajo: ${minutos} min` };
            }
        }
    }
    else if (msg.topic === "CONFIG_BREAK") {
        let minutos = parseInt(msg.payload);
        if (!isNaN(minutos) && minutos > 0 && minutos <= 60) {
            flow.set("BREAK_MINUTES", minutos);
            BREAK_MINUTES = minutos;
            BREAK_SECONDS = minutos * 60;
            if (state === "IDLE") {
                msgNotification = { payload: `‚öôÔ∏è Descanso corto: ${minutos} min` };
            }
        }
    }
    else if (msg.topic === "CONFIG_LONG_BREAK") {
        let minutos = parseInt(msg.payload);
        if (!isNaN(minutos) && minutos > 0 && minutos <= 60) {
            flow.set("LONG_BREAK_MINUTES", minutos);
            LONG_BREAK_MINUTES = minutos;
            LONG_BREAK_SECONDS = minutos * 60;
            if (state === "IDLE") {
                msgNotification = { payload: `‚öôÔ∏è Descanso largo: ${minutos} min` };
            }
        }
    }
    else if (msg.topic === "CONFIG_BUZZER") {
        let enabled = Boolean(msg.payload);
        flow.set("buzzerEnabled", enabled);
        msgNotification = { payload: `üîî Buzzer: ${enabled ? "activado" : "silenciado"}` };
    }
}

// ============================================================
// 2. COMANDOS
// ============================================================
else if (messageType === "COMMAND") {
    if (msg.payload === "CMD_START") {
        if (state === "IDLE") {
            startWork();
            presence = true;
            presentStreak = CONFIRM_COUNT;
            absentStreak = 0;
            flow.set("pomodoroPresence", true);
            flow.set("presentStreak", presentStreak);
            flow.set("absentStreak", 0);
            msgNotification = { payload: "üçÖ Ciclo iniciado" };
        }
    }
    else if (msg.payload === "CMD_CANCEL") {
        // Guardar trabajo parcial en stats
        if (state === "WORK" && workRemaining < WORK_SECONDS) {
            let workedSeconds = WORK_SECONDS - workRemaining;
            minutesWorkedToday += Math.floor(workedSeconds / 60);
        }

        state = "IDLE";
        workRemaining = 0;
        breakRemaining = 0;
        presence = false;
        presentStreak = 0;
        absentStreak = 0;
        breakPaused = false;
        breakPresenceHold = 0;
        isLongBreak = false;

        flow.set("pomodoroPresence", false);
        flow.set("presentStreak", 0);
        flow.set("absentStreak", 0);
        flow.set("isLongBreak", false);

        sendLcd("Cancelado", "Ac√©rcate...");
        sendBuzzer(100, 50, 1);
        msgNotification = { payload: "‚ùå Ciclo cancelado" };
    }
    else if (msg.payload === "CMD_PAUSE") {
        if (state === "WORK") {
            state = "EARLY_LEAVE";
            sendLcd("Pausado", formatTime(workRemaining));
            sendBuzzer(300, 300, 0);
            msgNotification = { payload: "‚è∏Ô∏è Pausado" };
        }
        else if (state === "EARLY_LEAVE") {
            state = "WORK";
            presence = true;
            flow.set("pomodoroPresence", true);
            sendLcd("Trabaja", "Restan " + formatTime(workRemaining));
            sendBuzzer(0, 0, 0);
            msgNotification = { payload: "‚ñ∂Ô∏è Reanudado" };
        }
    }
    else if (msg.payload === "CMD_DND_15") {
        // No molestar por 15 minutos
        doNotDisturb = true;
        dndUntil = Date.now() + (15 * 60 * 1000);
        flow.set("doNotDisturb", true);
        flow.set("dndUntil", dndUntil);
        msgNotification = { payload: "üîï Silencio por 15 min" };
        sendBuzzer(0, 0, 0); // Detener alarmas actuales
    }
    else if (msg.payload === "CMD_DND_OFF") {
        doNotDisturb = false;
        flow.set("doNotDisturb", false);
        flow.set("dndUntil", 0);
        msgNotification = { payload: "üîî Sonido activado" };
    }
    else if (msg.payload === "CMD_SKIP_BREAK") {
        // Saltar descanso actual
        if (state === "BREAK") {
            state = "IDLE";
            breakRemaining = 0;
            sendLcd("Listo", "Ac√©rcate");
            msgNotification = { payload: "‚è≠Ô∏è Descanso omitido" };
        }
    }
}

// ============================================================
// 3. SENSORES
// ============================================================
else if (messageType === "SENSOR") {
    if (msg.topic === "pomodoro/sensor/pir") {
        lastPir = (msg.payload === "true" || msg.payload === true);
        flow.set("lastPir", lastPir);
    }
    else if (msg.topic === "pomodoro/sensor/distance") {
        let dist = parseFloat(msg.payload);
        lastDistance = (isNaN(dist) || dist < 0) ? 999 : dist;
        flow.set("lastDistance", lastDistance);
    }

    // === DEBOUNCE ===
    let nowPresent = (lastPir && lastDistance > 0 && lastDistance < DIST_UMBRAL_CM);

    if (nowPresent) {
        presentStreak++;
        absentStreak = 0;

        if (!presence && presentStreak >= CONFIRM_COUNT) {
            presence = true;
            onPresenceConfirmed();
        }
    } else {
        absentStreak++;
        presentStreak = 0;

        if (presence && absentStreak >= CONFIRM_COUNT) {
            presence = false;
            onAbsenceConfirmed();
        }
    }

    flow.set("presentStreak", presentStreak);
    flow.set("absentStreak", absentStreak);
    flow.set("pomodoroPresence", presence);
}

// ============================================================
// 4. TICK (1 segundo)
// ============================================================
else if (messageType === "TICK") {
    // Verificar si expir√≥ el modo No Molestar
    if (doNotDisturb && Date.now() >= dndUntil) {
        doNotDisturb = false;
        flow.set("doNotDisturb", false);
        msgNotification = { payload: "üîî Fin del silencio" };
    }

    switch (state) {
        case "WORK":
            if (presence && workRemaining > 0) {
                workRemaining--;

                // Actualizar stats cada minuto
                if (workRemaining % 60 === 0) {
                    minutesWorkedToday++;
                    flow.set("minutesWorkedToday", minutesWorkedToday);
                }

                if (workRemaining % 5 === 0 || workRemaining < 10) {
                    sendLcd("Trabaja", "Restan " + formatTime(workRemaining));
                }

                if (workRemaining === 0) {
                    sendLcd("¬°Bien!", "Hora de descansar");
                    sendBuzzer(200, 200, 2);
                }
            }
            break;

        case "BREAK":
            if (presence) {
                if (breakPresenceHold < BREAK_GRACE_SECONDS) {
                    breakPresenceHold++;

                    if (breakPresenceHold === Math.floor(BREAK_GRACE_SECONDS / 2)) {
                        let remaining = BREAK_GRACE_SECONDS - breakPresenceHold;
                        sendLcd("¬°Al√©jate!", `(${remaining}s)`);
                    }

                    if (breakPresenceHold === BREAK_GRACE_SECONDS) {
                        breakPaused = true;
                        sendLcd("Descanso pausado", "Al√©jate");
                        sendBuzzer(300, 300, 0);
                        msgNotification = { payload: "‚ö†Ô∏è Descanso pausado" };
                    }
                }
            } else {
                breakPresenceHold = 0;
            }

            if (!breakPaused && breakRemaining > 0) {
                breakRemaining--;

                if (breakRemaining % 5 === 0 || breakRemaining < 10) {
                    let label = isLongBreak ? "Descanso largo" : "Descanso";
                    sendLcd(label, formatTime(breakRemaining));
                }

                if (breakRemaining === 0) {
                    sendLcd("¬°Listo!", "A trabajar");
                    sendBuzzer(150, 150, 3);
                    state = "IDLE";
                    presence = false;
                    isLongBreak = false;
                    flow.set("pomodoroPresence", false);
                    flow.set("isLongBreak", false);

                    cyclesToday++;
                    cyclesSinceBreak = 0; // Reset tras completar descanso
                    flow.set("cyclesToday", cyclesToday);
                    flow.set("cyclesSinceBreak", cyclesSinceBreak);

                    msgNotification = { payload: `‚úÖ Ciclo ${cyclesToday} completado` };
                }
            }
            break;

        case "EARLY_LEAVE":
        case "IDLE":
        default:
            break;
    }
}

// ============================================================
// EVENTOS DE TRANSICI√ìN
// ============================================================
function onPresenceConfirmed() {
    switch (state) {
        case "IDLE":
            if (workRemaining === 0 && breakRemaining === 0) {
                startWork();
                msgNotification = { payload: "üçÖ Auto-inicio" };
            }
            break;

        case "EARLY_LEAVE":
            state = "WORK";
            sendLcd("¬°Bien!", "Restan " + formatTime(workRemaining));
            sendBuzzer(0, 0, 0);
            msgNotification = { payload: "‚úÖ Reanudado" };
            break;

        case "BREAK":
            if (breakPresenceHold === 0) {
                sendLcd("Al√©jate", "Necesitas descansar");
                sendBuzzer(300, 300, 2);
            }
            break;

        case "WORK":
        default:
            break;
    }
}

function onAbsenceConfirmed() {
    switch (state) {
        case "WORK":
            if (workRemaining > 0) {
                state = "EARLY_LEAVE";
                sendLcd("¬øD√≥nde est√°s?", formatTime(workRemaining));
                sendBuzzer(300, 300, 0);
                msgNotification = { payload: "‚ö†Ô∏è Pausado por ausencia" };
            } else {
                // Trabajo completo, decidir tipo de descanso
                cyclesSinceBreak++;
                flow.set("cyclesSinceBreak", cyclesSinceBreak);

                if (cyclesSinceBreak >= CYCLES_BEFORE_LONG_BREAK) {
                    startBreak(true); // Long break
                } else {
                    startBreak(false); // Short break
                }
            }
            break;

        case "BREAK":
            if (breakPaused) {
                breakPaused = false;
                sendLcd("Descanso", formatTime(breakRemaining));
                sendBuzzer(0, 0, 0);
                msgNotification = { payload: "‚úÖ Descanso reanudado" };
            }
            breakPresenceHold = 0;
            break;

        case "EARLY_LEAVE":
        case "IDLE":
            break;
    }
}

// ============================================================
// FUNCIONES DE AYUDA
// ============================================================
function startWork() {
    workRemaining = WORK_SECONDS;
    state = "WORK";
    sendLcd("¬°Trabaja!", formatTime(workRemaining));
    sendBuzzer(150, 0, 1);
}

function startBreak(isLong) {
    isLongBreak = isLong;
    breakRemaining = isLong ? LONG_BREAK_SECONDS : BREAK_SECONDS;
    breakPaused = false;
    breakPresenceHold = 0;
    state = "BREAK";

    flow.set("isLongBreak", isLong);

    let label = isLong ? "¬°Descanso largo!" : "Descanso";
    let cycleInfo = isLong ? `(${cyclesSinceBreak} ciclos)` : "";

    sendLcd(label, "Al√©jate " + formatTime(breakRemaining));
    sendBuzzer(200, 200, isLong ? 3 : 2);

    if (isLong) {
        msgNotification = { payload: `üéâ ¬°Descanso largo! ${LONG_BREAK_MINUTES} min ${cycleInfo}` };
    }
}

function sendLcd(l1, l2) {
    msgLcd = { payload: { "l1": l1, "l2": l2 } };
}

function sendBuzzer(on_ms, off_ms, repeat) {
    // Jerarqu√≠a de silencio:
    // 1. Modo No Molestar (temporal) tiene prioridad
    // 2. Switch de Buzzer (permanente) segunda prioridad

    if (doNotDisturb) {
        // DND activo: silencio total (temporal)
        msgBuzzer = { payload: { "on_ms": 0, "off_ms": 0, "repeat": 0 } };
        return;
    }

    let buzzerEnabled = flow.get("buzzerEnabled") ?? true;
    if (!buzzerEnabled) {
        // Buzzer deshabilitado: silencio permanente
        msgBuzzer = { payload: { "on_ms": 0, "off_ms": 0, "repeat": 0 } };
        return;
    }

    // Ambos habilitados: hacer sonido
    msgBuzzer = { payload: { "on_ms": on_ms, "off_ms": off_ms, "repeat": repeat } };
}

function formatTime(s) {
    if (s < 0) s = 0;
    let m = Math.floor(s / 60);
    let ss = s % 60;
    return ("0" + m).slice(-2) + ":" + ("0" + ss).slice(-2);
}

// ============================================================
// GUARDAR ESTADO Y GENERAR SALIDAS
// ============================================================
flow.set("pomodoroState", state);
flow.set("workRemaining", workRemaining);
flow.set("breakRemaining", breakRemaining);
flow.set("breakPaused", breakPaused);
flow.set("breakPresenceHold", breakPresenceHold);
flow.set("cyclesToday", cyclesToday);
flow.set("cyclesSinceBreak", cyclesSinceBreak);
flow.set("minutesWorkedToday", minutesWorkedToday);

// Estado visual
let stateStr = state;
let timeStr = formatTime(state === "BREAK" ? breakRemaining : workRemaining);
let icon = "‚ö™";
let stateLabel = "Esperando";

switch (state) {
    case "WORK":
        icon = "üü¢";
        stateLabel = "Trabajando";
        break;
    case "BREAK":
        icon = isLongBreak ? "üíô" : "üîµ";
        stateLabel = isLongBreak ? "Descanso largo" : "Descanso corto";
        if (breakPaused) stateLabel += " (pausado)";
        break;
    case "EARLY_LEAVE":
        icon = "üü†";
        stateLabel = "Pausado";
        break;
    case "IDLE":
    default:
        icon = "‚ö™";
        stateLabel = "Esperando";
        break;
}

// Agregar modo DND al estado
if (doNotDisturb) {
    let minutesLeft = Math.ceil((dndUntil - Date.now()) / 60000);
    stateLabel += ` üîï(${minutesLeft}m)`;
}

msgUi = { payload: `${icon} ${stateLabel} | ${timeStr}` };

// Progreso
let percentage = 0;
if (state === "WORK" || state === "EARLY_LEAVE") {
    percentage = WORK_SECONDS > 0 ? (1 - workRemaining / WORK_SECONDS) * 100 : 0;
} else if (state === "BREAK") {
    let total = isLongBreak ? LONG_BREAK_SECONDS : BREAK_SECONDS;
    percentage = total > 0 ? (1 - breakRemaining / total) * 100 : 0;
}
msgGauge = { payload: Math.round(percentage) };

// Ciclos con indicador de progreso a long break
let cyclesText = `${cyclesToday} ciclos`;
if (cyclesSinceBreak > 0) {
    cyclesText += ` (${cyclesSinceBreak}/${CYCLES_BEFORE_LONG_BREAK} ‚Üí descanso largo)`;
}
msgCycles = { payload: cyclesText };

// --- Salida 7: Estad√≠sticas ---
let msgStats = {
    payload: `‚è±Ô∏è ${minutesWorkedToday} min trabajados hoy`
};

return [
    msgLcd,         // 1: LCD
    msgBuzzer,      // 2: Buzzer
    msgUi,          // 3: Estado
    msgGauge,       // 4: Progreso
    msgCycles,      // 5: Ciclos
    msgNotification,// 6: Notificaciones
    msgStats        // 7: Estad√≠sticas (NUEVO)
];
